unique_article_titles = {}

# New list to hold unique URLs
unique_urls = []
repeat_urls = []

for url in urls:
    # Check if the URL ends with a slash and remove it if present
    if url.endswith('/'):
        url = url[:-1]

    segments = url.split('/')
    last_segment = segments[-1]
    article_title = last_segment.split('/')[0]

    cleaned_title = re.sub('[^a-zA-Z]', '', article_title)

    # Check if the spliced text is already in the dictionary
    if cleaned_title not in unique_article_titles:
        unique_article_titles[cleaned_title] = True
        unique_urls.append(url)
    else:
        repeat_urls.append(url)

print(len(unique_urls), "unique urls were identified, while", len(repeat_urls), "were identified as repeat urls, for a percent of", (len(unique_urls)/len(urls))*100, "% were unique")
print("These are unique urls to check")
for url in unique_urls:
    print(url + '\n')

print("These are repeated urls to check")

for url in repeat_urls:
    print(url + '\n')

for titles in unique_article_titles:
    print(titles + '\n')